    RECURSIVE FUNCTION MNXNDET(n,A) RESULT(DET)
      IMPLICIT NONE
      INTEGER,INTENT(IN)::n
      REAL(KIND(1d0)),DIMENSION(n,n),INTENT(IN)::A
      REAL(KIND(1d0))::DET
      INTEGER::i
      SELECT CASE(n)
         CASE(1)
            DET=A(1,1)
            RETURN
         CASE(2)
            DET=M2X2DET(A)
            RETURN
         CASE(3)
            DET=M3X3DET(A)
            RETURN
         CASE(4)
            DET=M4X4DET(A)
            RETURN
         CASE(5)
            DET=M5X5DET(A)
            RETURN
         CASE(6)
            DET=M6X6DET(A)
            RETURN
         CASE DEFAULT
            DET=0d0
            DO i=1,n
               DET=DET+(-1)**(i+1)*A(1,i)*MNXNDET(n-1,MINOR(n,A,1,i))
            ENDDO
            RETURN
      END SELECT
    END FUNCTION MNXNDET

