         DO k2=1,n
            IF(k2.EQ.j)CYCLE
            MINOR(i0,j0)=A(k1,k2)
            j0=j0+1
         ENDDO
         i0=i0+1
      ENDDO
      RETURN
    END FUNCTION MINOR

    FUNCTION MINOR22(n,A,i1,i2,j1,j2)
      IMPLICIT NONE
      INTEGER,INTENT(IN)::n,i1,i2,j1,j2
      REAL(KIND(1d0)),DIMENSION(n,n),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(n-2,n-2)::MINOR22
      REAL(KIND(1d0)),DIMENSION(n-1,n-1)::A11
      INTEGER::imax,imin,jmax,jmin
      imax=MAX(i1,i2)
      imin=MIN(i1,i2)
      jmax=MAX(j1,j2)
      jmin=MIN(j1,j2)
      A11(1:n-1,1:n-1)=MINOR(n,A,imax,jmax)
      MINOR22(1:n-2,1:n-2)=MINOR(n-1,A11,imin,jmin)
      RETURN
    END FUNCTION MINOR22

    FUNCTION SIGNED_MINOR11(n,A,i,j)
      IMPLICIT NONE
      INTEGER,INTENT(IN)::n,i,j
      REAL(KIND(1d0)),DIMENSION(n,n),INTENT(IN)::A
      REAL(KIND(1d0))::SIGNED_MINOR11
      SIGNED_MINOR11=MNXNDET(n-1,MINOR(n,A,i,j))*(-1)**(i+j)
      RETURN
    END FUNCTION SIGNED_MINOR11

    FUNCTION SIGNED_MINOR22(n,A,i1,i2,j1,j2)
      IMPLICIT NONE
      INTEGER,INTENT(IN)::n,i1,i2,j1,j2
      REAL(KIND(1d0)),DIMENSION(n,n),INTENT(IN)::A
      REAL(KIND(1d0))::SIGNED_MINOR22
      IF(n.LT.2)THEN
         SIGNED_MINOR22=0d0
         RETURN
      ENDIF
      IF((i1-i2)*(j1-j2).EQ.0)THEN
         SIGNED_MINOR22=0d0
         RETURN
      ENDIF
      IF(n.EQ.2)THEN
         SIGNED_MINOR22=0d0
         IF((i1-j2).EQ.0.AND.(i2-j1).EQ.0)SIGNED_MINOR22=SIGNED_MINOR22+1d0
         IF((i1-j1).EQ.0.AND.(i2-j2).EQ.0)SIGNED_MINOR22=SIGNED_MINOR22-1d0
         RETURN
      ENDIF
      SIGNED_MINOR22=MNXNDET(n-2,MINOR22(n,A,i1,i2,j1,j2))*&
           (-1)**(i1+j1+i2+j2)*SIGN(1,i1-i2)*SIGN(1,j2-j1)
      RETURN
    END FUNCTION SIGNED_MINOR22

    FUNCTION Y_MATRIX_COLUMN_N(n,A,i,val) RESULT(res)
      ! replace all entries in the i-th column by val in n*n matrix A
      ! take the determinant
      IMPLICIT NONE
      INTEGER,INTENT(IN)::n,i
      REAL(KIND(1d0)),INTENT(IN)::val
      REAL(KIND(1d0)),DIMENSION(n,n),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(n,n)::Ai
      REAL(KIND(1d0))::res
      INTEGER::j
      DO j=1,n
         IF(j.EQ.i)THEN
            Ai(1:n,j)=A(1:n,j)
         ELSE
            Ai(1:n,j)=val
         ENDIF
      ENDDO
      res=MNXNDET(n,Ai)
    END FUNCTION Y_MATRIX_COLUMN_N

    SUBROUTINE M2X2INV(A,AINV,OK_FLAG) 
      IMPLICIT NONE
      REAL(KIND(1d0)),DIMENSION(2,2),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(2,2),INTENT(OUT)::AINV
      LOGICAL,INTENT(OUT)::OK_FLAG
      REAL(KIND(1d0)),PARAMETER::EPS = 1.0D-10
      REAL(KIND(1d0))::DET
      REAL(KIND(1d0)),DIMENSION(2,2)::COFACTOR
      DET=M2X2DET(A)
      IF (ABS(DET).LE.EPS)THEN
         AINV = 0.0D0
         OK_FLAG = .FALSE.
         RETURN
      END IF

      COFACTOR(1,1) = +A(2,2)
      COFACTOR(1,2) = -A(2,1)
      COFACTOR(2,1) = -A(1,2)
      COFACTOR(2,2) = +A(1,1)

      AINV = TRANSPOSE(COFACTOR) / DET
      OK_FLAG = .TRUE.
      RETURN
    END SUBROUTINE M2X2INV

    SUBROUTINE M3X3INV(A,AINV,OK_FLAG)
      IMPLICIT NONE
      REAL(KIND(1d0)),DIMENSION(3,3),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(3,3),INTENT(OUT)::AINV
      LOGICAL,INTENT(OUT)::OK_FLAG
