    SUBROUTINE MNXNINV(n,A,INVA,OK_FLAG)
      IMPLICIT NONE
      INTEGER,INTENT(IN)::n
      REAL(KIND(1d0)),DIMENSION(n,n),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(n,n),INTENT(OUT)::INVA
      LOGICAL,INTENT(OUT)::OK_FLAG
      REAL(KIND(1d0)),PARAMETER::EPS = 1.0D-10
      REAL(KIND(1d0))::DET
      REAL(KIND(1d0)),DIMENSION(n,n)::COFACTOR
      INTEGER::i,j
      SELECT CASE(n)
         CASE(1)
            DET=A(1,1)
            IF(ABS(DET).LT.EPS)THEN
               INVA=0d0
               OK_FLAG=.FALSE.
               RETURN
            ENDIF
            INVA=1d0/DET
            OK_FLAG=.TRUE.
            RETURN
         CASE(2)
            CALL M2X2INV(A,INVA,OK_FLAG)
            RETURN
         CASE(3)
            CALL M3X3INV(A,INVA,OK_FLAG)
            RETURN
         CASE(4)
            CALL M4X4INV(A,INVA,OK_FLAG)
            RETURN
         CASE(5)
            CALL M5X5INV(A,INVA,OK_FLAG)
            RETURN
         CASE(6)
            CALL M6X6INV(A,INVA,OK_FLAG)
            RETURN
         CASE DEFAULT
            DET=MNXNDET(n,A)
            IF(ABS(DET).LT.EPS)THEN
               INVA=0d0
               OK_FLAG=.FALSE.
               RETURN
            ENDIF
            DO i=1,n
               DO j=1,n
                  COFACTOR(j,i)=(-1)**(i+j)*MNXNDET(n-1,MINOR(n,A,i,j))
               ENDDO
            ENDDO
            INVA=COFACTOR/ DET
            OK_FLAG=.TRUE.
            RETURN
      END SELECT
    END SUBROUTINE MNXNINV

    FUNCTION rdot(N1,N2,A,b)
      ! A.b
      IMPLICIT NONE
      INTEGER,INTENT(IN)::N1,N2
      REAL(KIND(1d0)),DIMENSION(N1,N2),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(N2),INTENT(IN)::b
      REAL(KIND(1d0)),DIMENSION(N1)::rdot
      INTEGER::i,j
      DO i=1,N1
         rdot(i)=0d0
         DO j=1,N2
            rdot(i)=rdot(i)+A(i,j)*b(j)
         ENDDO
      ENDDO
      RETURN
    END FUNCTION rdot

    FUNCTION ldot(N1,N2,A,b)
      ! b.A 
      IMPLICIT NONE
      INTEGER,INTENT(IN)::N1,N2
      REAL(KIND(1d0)),DIMENSION(N1,N2),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(N1),INTENT(IN)::b
      REAL(KIND(1d0)),DIMENSION(N2)::ldot
      INTEGER::i,j
      DO i=1,N2
         ldot(i)=0d0
          DO j=1,N1
            ldot(i)=ldot(i)+A(j,i)*b(j)
         ENDDO
      ENDDO
      RETURN
    END FUNCTION ldot

    FUNCTION crdot(N1,N2,A,b)
      ! A.b, A is complex          
      IMPLICIT NONE
      INTEGER,INTENT(IN)::N1,N2
      COMPLEX(KIND(1d0)),DIMENSION(N1,N2),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(N2),INTENT(IN)::b
      COMPLEX(KIND(1d0)),DIMENSION(N1)::crdot
      INTEGER::i,j
      DO i=1,N1
         crdot(i)=DCMPLX(0d0)
         DO j=1,N2
            crdot(i)=crdot(i)+A(i,j)*b(j)
         ENDDO
      ENDDO
      RETURN
    END FUNCTION crdot

    FUNCTION cldot(N1,N2,A,b)
      ! b.A, A is complex          
      IMPLICIT NONE
      INTEGER,INTENT(IN)::N1,N2
      COMPLEX(KIND(1d0)),DIMENSION(N1,N2),INTENT(IN)::A
      REAL(KIND(1d0)),DIMENSION(N1),INTENT(IN)::b
      COMPLEX(KIND(1d0)),DIMENSION(N2)::cldot
      INTEGER::i,j
      DO i=1,N2
         cldot(i)=DCMPLX(0d0)
         DO j=1,N1
            cldot(i)=cldot(i)+A(j,i)*b(j)
         ENDDO
      ENDDO
      RETURN
    END FUNCTION cldot
    
    FUNCTION YNi(i,n,A)
      IMPLICIT NONE
      INTEGER,INTENT(IN)::i,n
      REAL(KIND(1d0)),DIMENSION(n,n),INTENT(IN)::A
      REAL(KIND(1d0))::YNi
      REAL(KIND(1d0)),DIMENSION(n,n)::At
      At(1:n,1:n)=A(1:n,1:n)
      At(i,1:n)=1d0
      YNi=MNXNDET(n,At)
      RETURN
    END FUNCTION YNi
    
    FUNCTION hdeltabar(n,x,j)
      IMPLICIT NONE
      INTEGER,INTENT(IN)::n,j
      INTEGER,DIMENSION(n),INTENT(IN)::x
      INTEGER::hdeltabar
      INTEGER::i
      DO i=1,n
         IF(x(i).NE.0.AND.i.EQ.j)THEN
            hdeltabar=0
            RETURN
         ENDIF
      ENDDO
      hdeltabar=1
      RETURN
    END FUNCTION hdeltabar
