     -           Csqrt2(-4*MW**2 + 
     -            MZ**2)))/
     -       (MW - MZ)**2 + 
     -      (72*MW**4*MZ**2*
     -         (MW + MZ)*
     -         (-24*MT**4*MW**2 - 
     -           24*MW**4*
     -            (2*MW**2 + MZ**2)
     -             + MT**2*
     -            (-10*MW**2*
     -            MZ**2 + 19*MZ**4)
     -           )*Pi**2*
     -         Csqrt2(-4*MW**2*
     -            MZ**2 + MZ**4)*
     -         log2(2*MW**2 + 
     -           MZ*
     -            (-MZ + 
     -            Csqrt2(-4*
     -           MW**2 + MZ**2))))/
     -       (MW - MZ)**2))/
     -  (MW**8*(MW + MZ)**3)
      ! the correct piece
      res=res+(-0.0010554289962743518*
     -    EL**4*(MT**2 + 2*MW**2)*
     -    (MW**2*(-36 + Pi**2) + 
     -      3*MT**2*(12 + Pi**2) - 
     -      6*(3*MT**2 + MW**2)*
     -       (li2(1 - 
     -           MT**2/MW**2) + 
     -         li2(MW**2/MT**2)) + 
     -      12*log2(MW/MT)*
     -       (9*MT**2 + 5*MW**2 - 
     -         (3*MT**2 + MW**2)*
     -          log2(1 - 
     -            MW**2/MT**2))))/
     -  (MW**2*Decay_R_SW2)
      Width_t2Wb_EW=prefactor*DBLE(res)
      IF(ischeme.EQ.1)RETURN
      ! Gmu scheme, diff/|Born ME|^2 = 2d0*dZe1_Gmu_MZ(0)
      ! because born ME squred is EL**2 ~ alpha
      resdiff=2d0*dZe1_Gmu_MZ(0)*3d0*EL**2*(MT**4+MT**2*MW**2-2d0*MW**4)
     $     /(2d0*MW**2*Decay_R_SW2)
      Width_t2Wb_EW=Width_t2Wb_EW+prefactor*DBLE(resdiff)
      RETURN
      END

      FUNCTION Width_t2Wb_bmass(MB)
      USE ParamModule
      USE Func_PSI
      IMPLICIT NONE
      REAL(KIND(1d0))::MB
      REAL(KIND(1d0))::Width_t2Wb_bmass
      REAL(KIND(1d0))::MT,MW,MH,MZ,EL
      COMPLEX(KIND(1d0))::res
      EL=Decay_e
      MT=Decay_MT
      MW=Decay_MW
      MH=Decay_MH
      MZ=Decay_MZ
      res=(0.0049735919716217296*
     -    EL**2*
     -    (-MT**6 + 
     -      3*MT**2*MW**4 - 
     -      2*MW**6 + 
     -      ((MB**2 - MT**2)**2 + 
     -         (MB**2 + MT**2)*
     -          MW**2 - 2*MW**4)*
     -       Csqrt2((MB - MT - MW)*
     -         (MB + MT - MW)*
     -         (MB - MT + MW)*
     -         (MB + MT + MW))))/
     -  (MT**3*MW**2*Decay_R_SW2)
      Width_t2Wb_bmass=DBLE(res)
      RETURN
      END

      FUNCTION Width_t2Wb2udxb_cms(idummy)
      USE ParamModule
      USE WDecay
      USE ZDecay
      USE Func_PSI
      IMPLICIT NONE
      INTEGER::idummy
      REAL(KIND(1d0))::Width_t2Wb2udxb_cms
      REAL(KIND(1d0))::MT,MW,MH,MZ,EL
      REAL(KIND(1d0))::GW,GZ
      COMPLEX(KIND(1d0))::res
      EL=Decay_e
      MT=Decay_MT
      MW=Decay_MW
      MH=Decay_MH
      MZ=Decay_MZ
      !GW=SMWWidth(1,1)
      !GZ=SMZWidth(1,1)
      ! use the LO width to get the correct finite-width effect
      GW=SMWWidth(0,0)
      GZ=SMZWidth(0,0)
      res=(0.00003149563909492138*
     -    EL**4*MZ**2*
     -    (GZ**2 + MZ**2)*
     -    ((MT**6 + 
     -         3*GW**2*MT**2*
     -          MW**2 - 
     -         3*(2*GW**2 + MT**2)*
     -          MW**4 + 2*MW**6)*
     -       atan(MW/GW) + 
     -      (MT**6 - 
     -         6*GW**2*MW**4 + 
     -         2*MW**6 + 
     -         3*MT**2*(GW - MW)*
     -          MW**2*(GW + MW))*
     -       atan((MT**2 - MW**2)/
     -         (GW*MW)) + 
     -      GW*MW*
     -       (-2*MT**4 + 
     -         4*MT**2*MW**2 + 
     -         MW**2*
     -          (GW**2 + 3*MT**2 - 
     -            3*MW**2)*
     -          (log2(MW**2*
     -            (GW**2 + MW**2))
     -            - log2(GW**2*
     -            MW**2 + 
     -            (MT**2 - MW**2)**
     -            2)))))/
     -  (GW*MT**3*MW*
     -    (GW**2*MW**2 + MW**4 - 
     -      2*GW*GZ*MW*MZ + 
     -      (GZ**2 - 2*MW**2)*
     -       MZ**2 + MZ**4))
      Width_t2Wb2udxb_cms=DBLE(res)
      RETURN
      END

      FUNCTION Width_t2Wb2udxb_dc_qcd(idummy)
      USE ParamModule
      IMPLICIT NONE
      INTEGER::idummy
      REAL(KIND(1d0))::Width_t2Wb2udxb_dc_qcd
      REAL(KIND(1d0))::MT,MW,MH,MZ,EL,gs
      COMPLEX(KIND(1d0))::res
      EL=Decay_e
      MT=Decay_MT
      MW=Decay_MW
      MH=Decay_MH
      MZ=Decay_MZ
      gs=Decay_gs
      res=(0.000027996123639930116*
     -    EL**2*gs**2*
     -    (MT**6 - 3*MT**2*MW**4 + 
     -      2*MW**6)*MZ**2)/
     -  (MT**3*MW**2*
     -    (MW**2 - MZ**2))
      Width_t2Wb2udxb_dc_qcd=DBLE(res)
      RETURN
      END

      FUNCTION Width_t2Wb2udxb_dc_EW(ischeme)
      USE ParamModule
      USE WDecay
      USE Func_PSI
      IMPLICIT NONE
      INTEGER::ischeme
      REAL(KIND(1d0))::Width_t2Wb2udxb_dc_EW
      REAL(KIND(1d0))::MT,MW,MH,MZ,EL,GWQED
      COMPLEX(KIND(1d0))::res
      EL=Decay_e
      MT=Decay_MT
      MW=Decay_MW
      MH=Decay_MH
      MZ=Decay_MZ
      GWQED=SMWWidth_EW(ischeme)/EL**4
C      res=(-3.4995154549912645e-6*
C     -    EL**4*
C     -    (9*MT**6*MW*MZ**4 + 
C     -      27*MT**4*MW**3*MZ**4 - 
C     -      54*MT**2*MW**5*MZ**4 + 
C     -      256*GWQED*MT**6*MW**4*
C     -       Pi**3 - 
C     -      768*GWQED*MT**2*MW**8*
C     -       Pi**3 + 
C     -      512*GWQED*MW**10*
C     -       Pi**3 - 
C     -      512*GWQED*MT**6*MW**2*
C     -       MZ**2*Pi**3 + 
C     -      1536*GWQED*MT**2*MW**6*
C     -       MZ**2*Pi**3 - 
C     -      1024*GWQED*MW**8*MZ**2*
C     -       Pi**3 + 
C     -      256*GWQED*MT**6*MZ**4*
C     -       Pi**3 - 
C     -      768*GWQED*MT**2*MW**4*
C     -       MZ**4*Pi**3 + 
C     -      512*GWQED*MW**6*MZ**4*
C     -       Pi**3 + 
C     -      108*MW**5*
C     -       (-MT**2 + MW**2)*
C     -       MZ**4*log2(MW) + 
C     -      54*MW**5*
C     -       (MT**2 - MW**2)*MZ**4*
C     -       log2(MT**2 - MW**2)))/
C     -  (MT**3*MW**3*
C     -    (MW**2 - MZ**2)**2)
C     the reason is only the one proportional to GWQED term is double-counted
C     the remaining term proportional to LO width is a pure finite-width effect
C     which cannot be cancelled in resonance region !!!
      res=(-0.027777777777777776*
     -    EL**4*GWQED*
     -    (MT**6 - 3*MT**2*MW**4 + 
     -      2*MW**6))/(MT**3*MW**3)
      Width_t2Wb2udxb_dc_EW=DBLE(res)
      RETURN
      END
